SELECT film.id_genre AS id_genre, genre.name AS name_genre, film.id_distrib AS id_distrib, distrib.name AS 'name_distrib', title AS title_film from film left JOIN genre ON genre.id_genre=film.id_genre LEFT JOIN distrib on distrib.id_distrib=film.id_distrib WHERE film.id_genre >= 4 AND film.id_genre <= 8;